-- Translate between the English strings we use internally and the localized 
-- strings.  Return the English for strings for which we do not have
-- translations.
function DestroDummies:xlateString (string)
    if DestroDummies.string[string] == nil then
        -- We are missing a translation.  Just return the English
        return string;
    end

    return DestroDummies.string[string];
end


-- A debugging routine to find a spell number, given the name
function DestroDummies:findSpellNo (string)
    local cur, max, spell;

    max = 200000		-- the highest number to try
    for cur = 0, max do
        spell = GetSpellInfo(cur);
	if spell == string then
	    print("Spell ", spell, " is number ", cur);
	    return;
    	end
    end
    print("Spell ", string, " is unknown");
end


-- A debugging routine to verify that all the spell numbers are correct
-- This is never called--it is provided as a test mechanism for the author
function DestroDummies:verifyCodes ()
    local cur, curSpid, curSpell;
    for cur = 0, (spellIndex - 1) do
        curSpell = knownSpellIndex[cur];
        curSpid = English2SpellIndex[curSpell];

        local name = GetSpellInfo(curSpid);
        if name ~= curSpell then
            print("Destruction for Dummies: incorrect spell number ", curSpid, ": my name: ", curSpell, ", WoW Name: ", name);
        end
    end
end
